using CodeModel;
namespace CodeModel {
	public class DataItem : NamedElement {
		public string type;

		public DataItem(){}

		public string ToCode(int i = 0){
			string code = Utils.ToCamelCase(type) + (i>0 ? i.ToString() : "");
			code += " : " + Utils.ToPascalCase(type);
			return code;
		}
	}
}